  .color-blind-mode {
    --wordle-correct: hsl(220, 85%, 45%);
    --wordle-present: hsl(35, 90%, 50%);
    --wordle-absent: hsl(0, 0%, 50%);
  }

  /* Accessibility: Reduced Motion */
  .reduce-motion * {
    animation-duration: 0.01ms !important;
    animation-iteration-count: 1 !important;
    transition-duration: 0.01ms !important;
  }

  /* Font size scaling */
  body {
    font-size: calc(1rem * var(--font-size-multiplier));
  }

  .tile {
    @apply aspect-square rounded-xl flex items-center justify-center font-bold shadow-lg transition-all duration-300 border-4 border-white/50 backdrop-blur-sm;
    transform-style: preserve-3d;
    font-size: clamp(1rem, 4vw, 1.5rem);
  }

  .tile-empty {
    @apply bg-white/30 border-white/40 text-gray-600;
  }

  .tile-current {
    @apply bg-gradient-to-br from-blue-300 to-cyan-400 border-blue-400 text-white shadow-xl;
  }

  .tile-correct {
    @apply bg-gradient-to-br from-green-400 to-emerald-500 border-green-300 text-white shadow-xl;
  }

  .tile-present {
    @apply bg-gradient-to-br from-yellow-400 to-orange-400 border-yellow-300 text-white shadow-xl;
  }

  .tile-absent {
    @apply bg-gradient-to-br from-gray-400 to-slate-500 border-gray-300 text-white shadow-xl;
  }

  .keyboard-key {
    @apply rounded-xl font-black transition-all duration-300 shadow-lg border-2 border-white/30 flex items-center justify-center;
    height: clamp(2.5rem, 8vh, 3rem);
    min-width: clamp(1.8rem, 6vw, 2.5rem);
    font-size: clamp(1rem, 4vw, 1.25rem);
    font-weight: 900;
  }

  .keyboard-key-default {
    @apply bg-gradient-to-br from-white/80 to-gray-100/80 hover:from-gray-100 hover:to-gray-200 text-gray-800 backdrop-blur-sm;
  }

  .keyboard-key-correct {
    @apply bg-gradient-to-br from-green-400 to-emerald-500 text-white shadow-xl;
  }

  .keyboard-key-present {
    @apply bg-gradient-to-br from-yellow-400 to-orange-400 text-white shadow-xl;
  }

  .keyboard-key-absent {
    @apply bg-gradient-to-br from-gray-400 to-slate-500 text-white shadow-xl;
  }

  .keyboard-key-special {
    @apply bg-gradient-to-br from-purple-500 to-pink-600 hover:from-purple-600 hover:to-pink-700 text-white shadow-xl flex items-center justify-center;
    padding: 0 clamp(0.5rem, 2vw, 0.75rem);
    font-size: clamp(0.625rem, 2.5vw, 0.75rem);
  }
}

@layer utilities {
  .animate-flip {
    animation: flip 0.6s ease-in-out;
  }

  .animate-bounce-in {
    animation: bounceIn 0.6s ease-out;
  }

  .animate-shake {
    animation: shake 0.5s ease-in-out;
  }

  .animate-confetti {
    animation: confetti 0.5s ease-out;
  }

  @keyframes flip {
    0% { transform: rotateY(0); }
    50% { transform: rotateY(-90deg); }
    100% { transform: rotateY(0); }
  }

  @keyframes bounceIn {
    0% { transform: scale(0.3); opacity: 0; }
    50% { transform: scale(1.05); }
    70% { transform: scale(0.9); }
    100% { transform: scale(1); opacity: 1; }
  }

  @keyframes shake {
    0%, 100% { transform: translateX(0); }
    10%, 30%, 50%, 70%, 90% { transform: translateX(-2px); }
    20%, 40%, 60%, 80% { transform: translateX(2px); }
  }

  @keyframes confetti {
    0% { transform: translateY(0) rotate(0deg); opacity: 1; }
    100% { transform: translateY(-100px) rotate(180deg); opacity: 0; }
  }

  /* Falling letters animations */
  @keyframes fall-0 {
    0% {
      transform: translateY(-100vh) rotate(0deg);
      opacity: 0.1;
    }
    10% {
      opacity: 0.3;
    }
    90% {
      opacity: 0.3;
    }
    100% {
      transform: translateY(100vh) rotate(360deg);
      opacity: 0;
    }
  }

  @keyframes fall-1 {
    0% {
      transform: translateY(-100vh) rotate(0deg);
      opacity: 0.1;
    }
    10% {
      opacity: 0.2;
    }
    90% {
      opacity: 0.2;
    }
    100% {
      transform: translateY(100vh) rotate(-360deg);
      opacity: 0;
    }
  }

  @keyframes fall-2 {
    0% {
      transform: translateY(-100vh) rotate(0deg) scale(0.8);
      opacity: 0.1;
    }
    10% {
      opacity: 0.25;
    }
    90% {
      opacity: 0.25;
    }
    100% {
      transform: translateY(100vh) rotate(180deg) scale(1.2);
      opacity: 0;
    }
  }

  @keyframes fall-3 {
    0% {
      transform: translateY(-100vh) rotate(0deg) scale(1.2);
      opacity: 0.1;
    }
    10% {
      opacity: 0.15;
    }
    90% {
      opacity: 0.15;
    }
    100% {
      transform: translateY(100vh) rotate(-180deg) scale(0.8);
      opacity: 0;
    }
  }
}

