import { motion } from "framer-motion";
import { Delete } from "lucide-react";
import { KeyState } from "@/lib/game-utils";

interface KeyboardProps {
  onKeyPress: (key: string) => void;
  onEnter: () => void;
  onBackspace: () => void;
  keyboardState: Record<string, KeyState>;
  disabled: boolean;
  blindMode?: boolean;
}

const KEYBOARD_ROWS = [
  ['Q', 'W', 'E', 'R', 'T', 'Y', 'U', 'I', 'O', 'P'],
  ['A', 'S', 'D', 'F', 'G', 'H', 'J', 'K', 'L'],
  ['ENTER', 'Z', 'X', 'C', 'V', 'B', 'N', 'M', 'BACKSPACE']
];

export default function Keyboard({ onKeyPress, onEnter, onBackspace, keyboardState, disabled, blindMode = false }: KeyboardProps) {
  const getKeyClass = (key: string) => {
    const baseClass = "keyboard-key";

    if (key === 'ENTER' || key === 'BACKSPACE') {
      return `${baseClass} keyboard-key-special`;
    }

    // In blind mode, all keys use default styling regardless of state
    if (blindMode) {
      return `${baseClass} keyboard-key-default`;
    }

    const state = keyboardState[key] || 'default';
    switch (state) {
      case 'correct': return `${baseClass} keyboard-key-correct`;
      case 'present': return `${baseClass} keyboard-key-present`;
      case 'absent': return `${baseClass} keyboard-key-absent`;
      default: return `${baseClass} keyboard-key-default`;
    }
  };

  const handleKeyClick = (key: string) => {
    if (disabled) return;

    if (key === 'ENTER') {
      onEnter();
    } else if (key === 'BACKSPACE') {
      onBackspace();
    } else {
      onKeyPress(key);
    }
  };

  return (
    <motion.div 
      className="space-y-2 sm:space-y-3 px-8 py-4 bg-white/20 backdrop-blur-sm rounded-2xl border-4 border-white/30 shadow-2xl"
      initial={{ opacity: 0, y: 30 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5, delay: 0.4 }}
    >
      {KEYBOARD_ROWS.map((row, rowIndex) => (
        <div key={rowIndex} className="flex justify-center gap-1.5 sm:gap-2">
          {row.map((key) => (
            <motion.button
              key={key}
              className={getKeyClass(key)}
              onClick={() => handleKeyClick(key)}
              disabled={disabled}
              whileHover={{ 
                scale: disabled ? 1 : 1.1,
                y: disabled ? 0 : -2,
                boxShadow: disabled ? undefined : "0 8px 16px rgba(0,0,0,0.2)"
              }}
              whileTap={{ scale: disabled ? 1 : 0.95 }}
              data-testid={`key-${key.toLowerCase()}`}
            >
              {key === 'BACKSPACE' ? (
                <Delete className="w-3 h-3 sm:w-4 sm:h-4" />
              ) : (
                key
              )}
            </motion.button>
          ))}
        </div>
      ))}
    </motion.div>
  );
}

