          </Button>
          <Button 
            size="sm" 
            variant="outline" 
            onClick={onClose}
            data-testid="button-dismiss-suggestion"
          >
            Dismiss
          </Button>
        </div>
      </AlertDescription>
    </Alert>
  );
}

