// Combined word list for validation (includes both answers and guesses)
export const ALL_OFFICIAL_WORDS = [...OFFICIAL_WORDLE_ANSWERS, ...OFFICIAL_WORDLE_GUESSES];

// Create a Set for fast lookup performance
export const OFFICIAL_WORDS_SET = new Set(ALL_OFFICIAL_WORDS);

// Function to check if a word is valid according to official Wordle rules
export function isOfficialWordleWord(word: string): boolean {
  return OFFICIAL_WORDS_SET.has(word.toLowerCase());
}

// Function to get a random answer word
export function getRandomAnswerWord(): string {
  const randomIndex = Math.floor(Math.random() * OFFICIAL_WORDLE_ANSWERS.length);
  return OFFICIAL_WORDLE_ANSWERS[randomIndex];
}

// Function to get the daily word (like official Wordle)
export function getDailyWord(date?: Date): string {
  const startDate = new Date('2021-06-19'); // Original Wordle start date
  const currentDate = date || new Date();
  const daysSinceStart = Math.floor((currentDate.getTime() - startDate.getTime()) / (1000 * 60 * 60 * 24));
  const wordIndex = daysSinceStart % OFFICIAL_WORDLE_ANSWERS.length;
  return OFFICIAL_WORDLE_ANSWERS[wordIndex];
}

export default {
  OFFICIAL_WORDLE_ANSWERS,
  OFFICIAL_WORDLE_GUESSES,
  ALL_OFFICIAL_WORDS,
  OFFICIAL_WORDS_SET,
  isOfficialWordleWord,
  getRandomAnswerWord,
  getDailyWord
};

